import React, { useState, useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { LanguageContext } from '../context/LanguageContext';
import { useAuth } from '../context/AuthContext';
import { translations as enTranslations } from '../translations/en';
import { translations as arTranslations } from '../translations/ar';
import './Auth.css';

const Login = () => {
  const { language } = useContext(LanguageContext);
  const { login } = useAuth();
  const navigate = useNavigate();
  const translations = language === 'ar' ? arTranslations : enTranslations;
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
    setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    // Basic validation
    if (!formData.email || !formData.password) {
      setError(translations.login.errorRequired);
      setLoading(false);
      return;
    }

    try {
      const result = await login(formData);

      if (result.success) {
        navigate('/');
      } else {
        setError(result.message || translations.login.errorLogin);
      }
    } catch (err) {
      setError(err.message || translations.login.errorLogin);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-page">
      <div className="container">
        <div className="auth-container">
          <div className="auth-header">
            <h1 className="auth-title">{translations.login.title}</h1>
            <p className="auth-subtitle">{translations.login.subtitle}</p>
          </div>

          <form className="auth-form" onSubmit={handleSubmit}>
            {error && <div className="error-message">{error}</div>}

            <div className="form-group">
              <label htmlFor="email">{translations.login.emailLabel}</label>
              <input
                type="email"
                id="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                required
                className="form-input"
                placeholder={translations.login.emailPlaceholder}
              />
            </div>

            <div className="form-group">
              <label htmlFor="password">{translations.login.passwordLabel}</label>
              <input
                type="password"
                id="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                required
                className="form-input"
                placeholder={translations.login.passwordPlaceholder}
              />
            </div>

            <div className="form-options">
              <label className="checkbox-label">
                <input type="checkbox" />
                <span>{translations.login.rememberMe}</span>
              </label>
              <Link to="/forgot-password" className="forgot-link">
                {translations.login.forgotPassword}
              </Link>
            </div>

            <button type="submit" className="auth-button" disabled={loading}>
              {loading ? translations.login.loading || 'Loading...' : translations.login.submitButton}
            </button>

            <div className="auth-footer">
              <p>
                {translations.login.noAccount}{' '}
                <Link to="/register" className="auth-link">
                  {translations.login.registerLink}
                </Link>
              </p>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Login;
